//! Provides an event system for the window crate.

use uuid::Uuid;
use wolf_engine_events::dynamic::Event;
use wolf_engine_input::Input;

use crate::{error::WindowError, WindowSettings};

/// An event generated by the window system.
#[derive(Clone, PartialEq, Debug)]
#[non_exhaustive]
pub enum WindowEvent {
    Started,
    Exited,
    EventsCleared,
    Input(Option<Uuid>, Input),
    WindowCreated(Uuid, Result<(), WindowError>),
    WindowRedrawRequested(Uuid),
    WindowClosed(Uuid),
    WindowResized(Uuid, u32, u32),
}

impl Event for WindowEvent {}

#[derive(Clone, PartialEq, Debug)]
pub enum WindowContextEvent {
    WindowResized(Uuid, u32, u32),
}

#[derive(Clone, PartialEq, Debug)]
pub enum WindowFrontendEvent {
    WindowCreated(Uuid, WindowSettings),
}

impl Event for WindowFrontendEvent {}
